import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import ReactHtmlParser from 'react-html-parser';

class PageCopy extends Component {
    
    constructor(){
        super();
        this.state = {
            urlToClone : '',
            boddy: ''
        }
    }

    componentDidMount () {
        debugger

        let url = this.props.location.pathname + this.props.location.search
        
        //logica para clonar la pagina
        let urlAClonar = 'https:/' + url

        
        console.log('El url a consumir por axios es: ' + urlAClonar);
        
        const principal = this;
        axios.get(urlAClonar)
        .then(function (response) {
            console.log(response);
            console.log('Recibimos el response' + response.data);

            principal.setState({
                boddy: response.data,
                urlToClone : urlAClonar
            })

        })
        .catch(function (error) {
            console.log(error);
        });
    }

  render() {
    return (
        <div className="container">
            { ReactHtmlParser(this.state.boddy) }
        </div>
    );
  }
}

export default PageCopy;
